import React, { useState } from "react";
import axios from "axios";
import Swal from 'sweetalert2';
import config from "../../config";

export default function AddRestaurant() {
    const [data, setData] = useState({
        Name: "",
        Address: "",
        Telephone: "",
    });

    function handleChange(evt) {
        const name = evt.target.name;
        const value = evt.target.value;
        setData({
            ...data,
            [name]: value,
        });
    }

    const onSubmitForm = async (e) => {
        e.preventDefault();

        // Basic validation
        if (!data.Name || !data.Address || !data.Telephone) {
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'Please fill out all fields.',
            });
            return;
        }

        try {
            const res = await axios({
                method: "post",
                baseURL: config.apiUrl,
                url: "/api/restaurant/create",
                data: data,
            });

            console.log(res.data);

            Swal.fire({
                icon: 'success',
                title: 'Success!',
                text: 'Data Saved Successfully!',
            }).then(() => {
                window.location.assign("/");
            });
        } catch (error) {
            console.log(error);

            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'An error occurred while saving the data. Please try again.',
            });
        }
    };

    return (
        <div>
            <div className="container">
                <form className="row g-3" noValidate onSubmit={onSubmitForm} id="formSubject">
                    <div className="col-auto">
                        <label htmlFor="inputText2" className="visually-hidden">Restaurant Name</label>
                        <input type="text" className="form-control" id="inputText2" placeholder="Enter Restaurant Name"
                            name="Name"
                            value={data.Name}
                            onChange={handleChange} />
                    </div>
                    <div className="col-auto">
                        <label htmlFor="inputText2" className="visually-hidden">Address</label>
                        <input type="text" className="form-control" id="inputText2" placeholder="Enter Address"
                            name="Address"
                            value={data.Address}
                            onChange={handleChange} />
                    </div>
                    <div className="col-auto">
                        <label htmlFor="inputText2" className="visually-hidden">Telephone</label>
                        <input type="text" className="form-control" id="inputText2" placeholder="Enter Telephone"
                            name="Telephone"
                            value={data.Telephone}
                            onChange={handleChange} />
                    </div>
                    <div className="col-auto">
                        <button type="submit" value="Submit" className="btn btn-primary mb-3">Add Restaurant</button>
                    </div>
                </form>
            </div>
        </div>
    );
}
