import React, { useState } from "react";
import axios from "axios";
import Swal from 'sweetalert2';
import config from "../../config";

export default function AddRestaurant() {
    const [data, setData] = useState({
        Name: "",
        Address: "",
        Telephone: "",
      });
    
      function handleChange(evt) {
        const name = evt.target.name;
        const value = evt.target.value;
        setData({
          ...data,
          [name]: value,
        });
      }
    
      const onSubmitForm = async (e) => {
        try {
            e.preventDefault();

            
    
            const res = await axios({
                method: "post",
                baseURL: config.apiUrl,
                url: "/api/restaurant/create",
                data: data,
                
            });
    
            console.log(res.data);
            
            
            Swal.fire({
                icon: 'success',
                title: 'Success!',
                text: 'Data Saved Successfully!',
            }).then(() => {
                window.location.assign("/");
            });
        } catch (error) {
            console.log(error);
    
            
            Swal.fire({
                icon: 'error',
                title: 'Error',
                text: 'An error occurred while saving the data. Please try again.',
            });
        }
    };
    

      
  return (
    <div>

        
     
      <div className="container" >
        
           <form class="row g-3" oValidate onSubmit={(e) => onSubmitForm(e)} id="formSubject">
  
  <div class="col-auto">
    <label for="inputText2" class="visually-hidden">Restaurant Name</label>
    <input type="text" class="form-control" id="inputText2" placeholder="Enter Restaurant Name"
    name="Name"
    value={data.Name}
    onChange={handleChange}/>
  </div>

  <div class="col-auto">
    <label for="inputText2" class="visually-hidden">Address</label>
    <input type="text" class="form-control" id="inputText2" placeholder="Enter Address"
    name="Address"
    value={data.Address}
    onChange={handleChange}/>
  </div>

  <div class="col-auto">
    <label for="inputText2" class="visually-hidden">Telephone</label>
    <input type="text" class="form-control" id="inputText2" placeholder="Enter Telephone "
    name="Telephone"
    value={data.Telephone}
    onChange={handleChange}/>
  </div>

  <div class="col-auto">
    <button type="submit" value="Submit" class="btn btn-primary mb-3">Add Restaurant</button>
  </div>
</form>

      </div>
   
    </div>
  )
}
