import React, { useState, useEffect } from "react";
import axios from "axios";
import Swal from 'sweetalert2';
import config from "../../config";
import "./restaurant.css";
import { useParams, useHistory} from "react-router-dom";

export default function RestaurantList() {
  const [restaurants, setRestaurants] = useState([]);
  const [formData, setFormData] = useState({
    Name: "",
    Address: "",
    Telephone: ""
  });

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        console.log("User not authenticated.");
        return;
      }

      try {
        const tempData = await getData(token);
        setRestaurants(tempData);
        console.log(JSON.stringify(tempData));
      } catch (error) {
        console.log("Error fetching restaurant data:", error);
      }
    };
    fetchData();
  }, []);

  const getData = async (token) => {
    try {
      const finalURL = `${config.apiUrl}/api/restaurant/`;
      const res = await axios.get(finalURL, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const restaurant = res.data?.restaurant || [];

      return restaurant;
    } catch (error) {
      console.log("Error fetching Restaurant data:", error);
      throw error;
    }
  };

  const removeRestaurant = async (restaurantId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.log("User not authenticated. Redirecting to login page.");
        return;
      }

      const isConfirmed = await Swal.fire({
        title: 'Are you sure?',
        text: 'You want to delete this Restaurant?',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Yes, delete it!'
      });

      if (isConfirmed.isConfirmed) {
        const finalURL = `${config.apiUrl}/api/restaurant/${restaurantId}`;
        await axios.delete(finalURL, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        Swal.fire(
          'Deleted!',
          'The Restaurant has been deleted.',
          'success'
        ).then(() => {
          window.location.reload(false);
        });
      }
    } catch (error) {
      console.log("Error deleting Restaurant:", error);
      Swal.fire(
        'Error!',
        'Failed to delete the Restaurant.',
        'error'
      );
    }
  };

  function handleChange(evt) {
    const name = evt.target.name;
    const value = evt.target.value;
    setFormData({
      ...formData,
      [name]: value,
    });
  }

  const onSubmitForm = async (e) => {
    try {
      e.preventDefault();

      const token = localStorage.getItem("token");

      const res = await axios({
        method: "post",
        baseURL: config.apiUrl,
        url: "/api/restaurant/create",
        data: formData,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      console.log(res.data);

      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: 'Data Saved Successfully!',
      }).then(() => {
        window.location.assign("/");
      });
    } catch (error) {
      console.log(error);

      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'An error occurred while saving the data. Please try again.',
      });
    }
  };


  // edit part

  const { id } = useParams();
  const history = useHistory();
  const [restaurant, setRestaurant] = useState();
  
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`${config.apiUrl}/api/restaurant/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setRestaurant(res.data.restaurant);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [id, token]);

  function handleChange1(evt) {
    const { name, value } = evt.target;
    setRestaurant({
      ...restaurant,
      [name]: value,
    });
  }

  const onSubmitForm1 = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `${config.apiUrl}/api/restaurant/update/${id}`,
          restaurant,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: 'Updated Successfully!',
      }).then(() => {
        history.push("/");
        window.location.reload();
      });
    } catch (error) {
      console.log(error);
    }
  };
  
  if (!restaurant) {
    return <>Loading the data</>;
  }
  

  return (
    <div>


      {/* <!-- Modal 1 --> */}
      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Add Restaurant</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
              <form className="row g-3" onSubmit={onSubmitForm} id="formSubject">
                <div className="col-auto">
                  <label htmlFor="inputText2" className="visually-hidden">Restaurant Name</label>
                  <input type="text" className="form-control" id="inputText2" placeholder="Enter Restaurant Name"
                    name="Name"
                    value={formData.Name}
                    onChange={handleChange} />
                </div>

                <div className="col-auto">
                  <label htmlFor="inputText2" className="visually-hidden">Address</label>
                  <input type="text" className="form-control" id="inputText2" placeholder="Enter Address"
                    name="Address"
                    value={formData.Address}
                    onChange={handleChange} />
                </div>

                <div className="col-auto">
                  <label htmlFor="inputText2" className="visually-hidden">Telephone</label>
                  <input type="text" className="form-control" id="inputText2" placeholder="Enter Telephone "
                    name="Telephone"
                    value={formData.Telephone}
                    onChange={handleChange} />
                </div>

                <div className="col-auto">
                  <button type="submit" value="Submit" className="btn btn-success mb-3">Add</button>
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
            </div>
          </div>
        </div>
      </div>

        {/* <!-- Modal 2 --> */}
        <div className="modal fade" id="exampleModal-2" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Edit Restaurant</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
            <form class="row g-3" oValidate onSubmit={(e) => onSubmitForm1(e)} id="formSubject">

<div class="col-auto">
 <label for="inputText2" class="visually-hidden">Restaurant Name</label>
 <input type="text" class="form-control" id="inputText2" placeholder="Enter Restaurant Name"
 name="Name"
 defaultValue={restaurant.Name}
 onChange={handleChange1}/>
</div>

<div class="col-auto">
 <label for="inputText2" class="visually-hidden">Address</label>
 <input type="text" class="form-control" id="inputText2" placeholder="Enter Address"
 name="Address"
 defaultValue={restaurant.Address}
 onChange={handleChange1}/>
</div>

<div class="col-auto">
 <label for="inputText2" class="visually-hidden">Telephone</label>
 <input type="text" class="form-control" id="inputText2" placeholder="Enter Telephone"
 name="Telephone"
 defaultValue={restaurant.Telephone}
 onChange={handleChange1}/>
</div>
<div class="col-auto">
 <button type="submit" value="Submit" class="btn btn-success mb-3">Update</button>
</div>
</form>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
            </div>
          </div>
        </div>
      </div>




      <div className='container' id="RestaurantContainer">
 
        {/* <!-- Button trigger modal --> */}
        <button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
          Add Restaurant
        </button>

        <table className='table table-hover'>
          <thead>
            <tr>
              <th scope='col'><i className='fas fa-list'></i></th>
              <th scope='col'>Restaurant Name</th>
              <th scope='col'>Address</th>
              <th scope='col'>Telephone</th>
              <th scope='col'>Action</th>
            </tr>
          </thead>

          <tbody>
            {restaurants.map((restaurant, index) => (
              <tr key={index}>
                <th scope='row'>{index + 1}</th>
                <td>{restaurant.Name}</td>
                <td>{restaurant.Address}</td>
                <td>{restaurant.Telephone}</td>
                <td>
                  <a className='btn' data-bs-toggle="modal" data-bs-target="#exampleModal-2" id="editBtn" href={`/editRestaurant/${restaurant._id}`}>
                    <i className='fas fa-edit' id="editIcon"></i>
                  </a>
                  &nbsp;
                  <button className='btn' id="editDelete" onClick={() => removeRestaurant(restaurant._id)}>
                    <i className='fas fa-trash-alt' id="DeleteIcon"></i>&nbsp;
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
