import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useHistory} from "react-router-dom";
import Swal from 'sweetalert2';
import config from "../../config";

export default function EditRestaurant() {
  const { id } = useParams();
  const history = useHistory();
  const [restaurant, setRestaurant] = useState();
  
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`${config.apiUrl}/api/restaurant/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setRestaurant(res.data.restaurant);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [id, token]);

  function handleChange(evt) {
    const { name, value } = evt.target;
    setRestaurant({
      ...restaurant,
      [name]: value,
    });
  }

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `${config.apiUrl}/api/restaurant/update/${id}`,
          restaurant,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: 'Updated Successfully!',
      }).then(() => {
        history.push("/");
        window.location.reload();
      });
    } catch (error) {
      console.log(error);
    }
  };
  
  if (!restaurant) {
    return <>Loading the data</>;
  }
  
  return (
    <div>
      <div className="container" id="addNewContainer">
        
        <form class="row g-3" oValidate onSubmit={(e) => onSubmitForm(e)} id="formSubject">

<div class="col-auto">
 <label for="inputText2" class="visually-hidden">Restaurant Name</label>
 <input type="text" class="form-control" id="inputText2" placeholder="Enter Restaurant Name"
 name="Name"
 defaultValue={restaurant.Name}
 onChange={handleChange}/>
</div>

<div class="col-auto">
 <label for="inputText2" class="visually-hidden">Address</label>
 <input type="text" class="form-control" id="inputText2" placeholder="Enter Address"
 name="Address"
 defaultValue={restaurant.Address}
 onChange={handleChange}/>
</div>

<div class="col-auto">
 <label for="inputText2" class="visually-hidden">Telephone</label>
 <input type="text" class="form-control" id="inputText2" placeholder="Enter Telephone"
 name="Telephone"
 defaultValue={restaurant.Telephone}
 onChange={handleChange}/>
</div>
<div class="col-auto">
 <button type="submit" value="Submit" class="btn btn-success mb-3">Update</button>
</div>
</form>
    </div>
    </div>

  )
}
