import React, { useState } from "react";
import axios from "axios";
import Swal from 'sweetalert2';
import config from "../../config";

export default function AddRestaurant() {
  const [data, setData] = useState({
    Name: "",
    Address: "",
    Telephone: "",
  });

  function handleChange(evt) {
    const { name, value } = evt.target;
    setData({
      ...data,
      [name]: value,
    });
  }

  const onSubmitForm = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem("token");

      const res = await axios({
        method: "post",
        baseURL: config.apiUrl,
        url: "/api/restaurant/create",
        data: data,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      console.log(res.data);

      Swal.fire({
        icon: 'success',
        title: 'Success!',
        text: 'Data Saved Successfully!',
      }).then(() => {
        window.location.assign("/");
      });
    } catch (error) {
      console.log(error);

      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: error.response && error.response.data && error.response.data.message ? error.response.data.message : 'Please try again.',
      });
    }
  };

  return (
    <div className="container">
      <form onSubmit={onSubmitForm} noValidate>
        <div className="col-auto">
          <label htmlFor="restaurantName" className="visually-hidden">Restaurant Name</label>
          <input type="text" className="form-control" id="restaurantName" placeholder="Enter Restaurant Name"
            name="Name"
            value={data.Name}
            onChange={handleChange} />
        </div>

        <div className="col-auto">
          <label htmlFor="address" className="visually-hidden">Address</label>
          <input type="text" className="form-control" id="address" placeholder="Enter Address"
            name="Address"
            value={data.Address}
            onChange={handleChange} />
        </div>

        <div className="col-auto">
          <label htmlFor="telephone" className="visually-hidden">Telephone</label>
          <input type="text" className="form-control" id="telephone" placeholder="Enter Telephone"
            name="Telephone"
            value={data.Telephone}
            onChange={handleChange} />
        </div>

        <div className="col-auto">
          <button type="submit" value="submit" className="btn btn-primary mb-3">Add Restaurant</button>
        </div>
      </form>
    </div>
  )
}
